(function($) {
                    var context = {
                        channelId: null,
                        useThumbWatch: 0,
                        videoId: '1442837597',
                        lang: 'ja-jp',
                        area: 'JP',
                        watchTrackId: 'Uj364ii4N0_1471372260446'
