                    txt = txt.replace(/\r?\n/g, '<br>');
                    if (!this.isHtml())
                        txt = this.link(txt);
                    tags.push('<p class="font12">');
                    tags.push(txt);
                    tags.push('</p>');
                }

                return tags.join('\n');
            });
        })();
