(function($) {

                    // 表示時
                    $(document).ready(function() {
                        sendGuestWatchShowCommentLog();
                    });

                    // ログインボタンクリック時
                    $('.login-button').on('click', function(e) {
                        sendGuestWatchLoginButtonLog(e.target.id);
                    });

                    // 対象者の識別ログ
                    function sendGuestWatchShowCommentLog() {
                        var context = {
                            videoId: '1396710466'
                        };
                        var image = new Image();
                        image.src = createURL(context, 'GuestWatchShowCommentLog');
                    }

                    // クリックされたログインボタンの識別ログ
                    function sendGuestWatchLoginButtonLog(targetName) {
                        var context = {
                            loginButton: targetName
                        };
                        var image = new Image();
                        image.src = createURL(context, 'GuestWatchLoginButtonLog');
                    }

                    // ログのURLを生成する
                    function createURL(ctx, name) {
                        var url = 'http://www.nicovideo.jp/api/ria/log.gif?';
                        url += $.param($.extend({}, ctx, {
                            name: name,
                            from: 'watch_logout',
                            t: (new Date()).getTime()
                        }));
                        return url;
                    }

                })(jQuery);
{
  "@context" : "http://schema.org",
  "@type" : "WebSite",
  "name" : "niconico",
  "url" : "http://www.nicovideo.jp"
}
{
  "@context": "http://schema.org",
  "@type": "BreadcrumbList",
  "itemListElement": [{
    "@type": "ListItem",
    "position": 1,
