                var txt = this.getBannertext();
                if (this.isHtml()) {
                    txt = this.getAppend();
                }

                var tags = [];
                tags.push('<p style="margin: 0 0 2px;">');
                tags.push(this.getTag());
                tags.push('</p>');

                if (typeof txt === 'string' && txt.length) {
                    txt = txt.replace(/\r?\n/g, '<br>');
                    if (!this.isHtml())
                        txt = this.link(txt);
                    tags.push('<p class="font12">');
                    tags.push(txt);
                    tags.push('</p>');
                }

                return tags.join('\n');
            });
        })();
