<!--
        (function() {
            new Ads.Advertisement({
                zone: 816,
                segments: Ads.get_segment_object(),
                impression: 2
            }).set('web_pc_360', function () {
                var txt = this.getBannertext();
                if (this.isHtml()) {
                    txt = this.getAppend();
                }

                var tags = [];
                tags.push('<p style="margin: 0 0 2px;">');
                tags.push(this.getTag());
                tags.push('</p>');

                if (typeof txt === 'string' && txt.length) {
                    txt = txt.replace(/\r?\n/g, '<br>');
                    if (!this.isHtml())
                        txt = this.link(txt);
                    tags.push('<p class="font12">');
                    tags.push(txt);
                    tags.push('</p>');
                }

                return tags.join('\n');
            });
        })();
        -->
(function($) {
                    var context = {
                        channelId: null,
                        useThumbWatch: 0,
                        videoId: '1378986163',
                        lang: 'ja-jp',
                        area: 'JP',
                        watchTrackId: '0gpkz5bwnO_1471372280914'
                    };

                    var image = new Image();
                    image.src = createURL(context);

                    function createURL(ctx) {
                        var url = 'http://www.nicovideo.jp/api/ria/log.gif?';
                        url += $.param($.extend({}, ctx, {
                            name: 'WatchLogoutPVLog',
                            from: 'watch_logout',
                            t: (new Date()).getTime()
                        }));
                        return url;
                    }
                })(jQuery);
(function($) {

                    // 表示時
                    $(document).ready(function() {
                        sendGuestWatchShowCommentLog();
                    });

                    // ログインボタンクリック時
                    $('.login-button').on('click', function(e) {
                        sendGuestWatchLoginButtonLog(e.target.id);
                    });

                    // 対象者の識別ログ
                    function sendGuestWatchShowCommentLog() {
                        var context = {
                            videoId: '1378986163'
                        };
                        var image = new Image();
                        image.src = createURL(context, 'GuestWatchShowCommentLog');
                    }

                    // クリックされたログインボタンの識別ログ
                    function sendGuestWatchLoginButtonLog(targetName) {
                        var context = {
                            loginButton: targetName
                        };
                        var image = new Image();
                        image.src = createURL(context, 'GuestWatchLoginButtonLog');
                    }

                    // ログのURLを生成する
                    function createURL(ctx, name) {
                        var url = 'http://www.nicovideo.jp/api/ria/log.gif?';
                        url += $.param($.extend({}, ctx, {
                            name: name,
                            from: 'watch_logout',
                            t: (new Date()).getTime()
                        }));
                        return url;
                    }

                })(jQuery);
{
